import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import ldlogo from "./LaunchDarklyLogo.png";
import "./App.css";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { Redirect } from "react-router";
import {
  withLDProvider,
  useFlags,
  useLDClient,
} from "launchdarkly-react-client-sdk";

import Header from "./components/Header";
import configData from "./LaunchDarklyConfig.json";
import LoginPage from "./components/LoginPage";
import StartNowButton from "./components/StartNowButton";
import { InputForm } from "./components/InputForm";
import { getSpaceUntilMaxLength } from "@testing-library/user-event/dist/utils";
//import { LDProvider } from 'react';


function App() {
  const { logoSwicth, footerText, headerText, LDUser, EmailUser } = useFlags();
  //const  client  = useLDClient();
  const [email, setEmail] = useState("");
  const updateEmail = (eachEntry) => {
    setEmail(eachEntry);
  };

  //  const user = {
  //   key: 'anonymous',
  //   email : email
  // };

  return (
    <div className="App">
      <header className="App-header">
       <Header></Header> 

        {/* {logoSwicth ? (
          <img src={ldlogo} className="App-logo" alt="logo" />
        ) : (
          <img src={logo} className="App-logo" alt="logo" />
        )} */}
        <LogoInfo />
        <p> {footerText} </p>
        <InputForm updateEmail={updateEmail} />

        {EmailUser ? (
          <LoginPage></LoginPage>
        ) : (
          <StartNowButton></StartNowButton>
        )}
      </header>
    </div>
  );
}

export default withLDProvider({
  clientSideID: configData.REACT_APP_CLIENT_ID,
  // user: {
  //     "key": "aa0ceb",
  //     "name": "Grace Hopper",
  //   "email":  "pranali.u@gmail.com"
  // },
  options: {
    bootstrap: localStorage,
  },
})(App);
