import React from 'react'
import logo from './logo.svg';
import ldlogo from './LaunchDarklyLogo.png'
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { Redirect } from 'react-router';
import  { withLDProvider, useFlags } from 'launchdarkly-react-client-sdk';
import Header from './components/Header';
import configData from './LaunchDarklyConfig.json'
import LoginPage from './components/LoginPage';
import StartNowButton from './components/StartNowButton';


//import { LDProvider } from 'react';

function App() {
  const { logoSwicth, footerText, headerText } = useFlags();
  return (
    <div className="App">
      <header className="App-header">
        {
          headerText ? (<Header></Header>) : (<div/>)
        }

        { logoSwicth ? (<img src={ldlogo} className="App-logo" alt="logo" />) :
           ( <img src={logo} className="App-logo" alt="logo" /> )
        }
        <p> {footerText} </p>
        {LDUser ? ( <button> <a href="https://docs.launchdarkly.com/home/getting-started"> Start Now  </a> </button> )
               : (<button> <a href="https://app.launchdarkly.com/default/production/features"> Create your first feature flag  </a> </button> ) 
        } 
        </header>

    </div>
  );
}

export default withLDProvider({
  clientSideID: configData.REACT_APP_CLIENT_ID,
//   user: {
//     "key": "aa0ceb",
//     "name": "Grace Hopper",
//     "email": "gracehopper@example.com"
//   },
  options: { 
    bootstrap: localStorage 
  }
})(App);
