import React, { useState, useEffect } from "react";
import { Row, Col, Form, FormGroup, Label, Input, Button } from "reactstrap";
export const InputForm = props => {
    const { updateEmail } = props;

    const [eachEmail, setEmail] = useState("");
    const {email} = eachEmail;
    const handleInputChange = e => {
        setEmail(e.target.value);
      };

      const handleFinalSubmit = e => {
        e.preventDefault();

        updateEmail(eachEmail);
      };

  return (
  <div>


    <Row >
     <Col sm="12" md={{ size: 10, offset: 3 }}>

        <Form>

          <FormGroup>

            <Label for="Email">Enter Email </Label>

            <Input name="Email" placeholder="Ex: test@testdomain.com" onChange={handleInputChange} defaultValue={props.email} > </Input>

          </FormGroup>

          <Button onClick={handleFinalSubmit}>Submit</Button>
        </Form>
        </Col>
      </Row>
    <br/>
  </div> 
  );
};
export default InputForm;
