import React, { useState } from "react";
import { Row, Col, Form, FormGroup, Label, Input, Button } from "reactstrap";
export const InputForm = props => {
    const initialInputState = { email: "" };
    const [eachEmail, setEmail] = useState(initialInputState);
    const {email} = eachEmail;
    const handleInputChange = e => {
        setEmail({ ...eachEmail, [e.target.name]: e.target.value });
      };
      console.log(eachEmail);
  return (
  <div>


    <Row >
     <Col sm="12" md={{ size: 10, offset: 3 }}>

        <Form>

          <FormGroup>

            <Label for="Email">Enter Email </Label>

            <Input name="Email" placeholder="Ex: test@testdomain.com" onChange={handleInputChange} value={email}> </Input>

          </FormGroup>

          <Button>Submit</Button>
        </Form>
        </Col>
      </Row>
    <br/>
  </div> 
  );
};
export default InputForm;
