import React, { useState } from "react";
import logo from './logo.svg';
import ldlogo from './LaunchDarklyLogo.png'
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { Redirect } from 'react-router';
import  { withLDProvider, useFlags } from 'launchdarkly-react-client-sdk';
import Header from './components/Header';
import configData from './LaunchDarklyConfig.json'
import LoginPage from './components/LoginPage';
import StartNowButton from './components/StartNowButton';
import { InputForm } from './components/InputForm';


//import { LDProvider } from 'react';

function App() {
  const { logoSwicth, footerText, headerText, LDUser } = useFlags();
  const [email, setEmail] = useState([]);
  // const updateEmail = eachEmail => {   setEmail([...email, eachEmail]);
  const updateEmail = eachEmail => {   setEmail(eachEmail);

};
console.log("Email is:"+ email);
console.log("LDUser flag value is:" + LDUser);
  return (
    <div className="App">
      <header className="App-header">
        {
          headerText ? (<Header></Header>) : (<div/>)
        }

        { logoSwicth ? (<img src={ldlogo} className="App-logo" alt="logo" />) :
           ( <img src={logo} className="App-logo" alt="logo" /> )
        }
        <p> {footerText} </p>
        <InputForm updateEmail={updateEmail} />
        { LDUser ?(<LoginPage></LoginPage>) :  (<StartNowButton></StartNowButton>)
        } 
        </header>

    </div>
  );
}

export default withLDProvider({
  clientSideID: configData.REACT_APP_CLIENT_ID,
 // user: {
//     "key": "aa0ceb",
//     "name": "Grace Hopper",
 //   "email":  updateEmail 
  //},
  options: { 
    bootstrap: localStorage 
  }
})(App);
